mapper.insert:
INSERT INTO chat_room (
    book_id,
    seller_id,
    buyer_id
) VALUES (
             #{bookId},
             #{sellerId},
             #{buyerId}
         )

mapper.select:
SELECT seller_id
FROM book
WHERE book_id = #{bookId}

mapper.update:
UPDATE chat_room
SET is_closed = TRUE
WHERE book_id = #{bookId}

UPDATE book
SET status = #{status}
WHERE book_id = #{bookId}
